INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Decret d''Alcaldia/ Decret de Presidència', 'TD01-002', 1, 'Document resolutiu ordenat per l''alcalde o el president de la corporació per donar resposta a certs procediments o tràmits necessaris per al govern d''un municipi.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Ordre', 'TD01-003', 1, 'Document relatiu a una disposició reglamentària dictada per part de l''administració del govern autonòmic i estatal en l''àmbit de les pròpies competències per regular un àmbit concret o una matèria determinada', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Ordenança', 'TD01-004', 1, 'Document relatiu a una disposició general de caràcter reglamentari dictada normalment per l''administració local en l''àmbit de les seves competències', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Reglament', 'TD01-005', 1, 'Document que conté un conjunt ordenat de normes o preceptes dictats pel poder executiu que té la potestat reglamentària per a l''execució d''una llei, el funcionament d''una corporació, d''un servei o de qualsevol activitat', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Instrucció', 'TD53-001', 1, 'Document on es mostra l''ordre o explicació donada pels òrgans administratius per dirigir les activitats dels seus òrgans jeràrquicament dependents', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Resolució', 'TD01-006', 1, 'Document mitjançant el qual es presenta la determinació presa per un òrgan administratiu o judicial sobre un afer determinat quan finalitza el procediment administratiu. NOTA. S''anomenen Acords quan els actes administratius resolutoris provenen del Govern o dels òrgans de govern de  les corporacions locals', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Laude', 'TD01-007', 1, 'Document que reflecteix una decisió dictada per àrbitres de dret o d''equitat als quals ha estat sotmès un afer', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Autorització', 'TD01-008', 1, 'Document referent a l''acte pel qual una autoritat administrativa, legislativa o judicial o una persona física o jurídica autoritza a una altra autoritat, a un funcionari que en depèn o a un particular, l''exercici de determinats drets.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llicència', 'TD01-009', 1, 'Document referent a l''autorització atorgada per part de l''administració per a la realització d''activitats que, segons l''ordenament jurídic, resten subjectes a aquest requisit previ.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Permís', 'TD01-010', 1, 'Document pel qual es realitza un consentiment formal o una autorització per expresar o realitzar quelcom', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Nomenament', 'TD01-011', 1, 'Document que constata l''elecció d''una persona per prendre possessió d''un càrrec o funció determinats', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Estatuts', 'TD01-012', 1, 'Document que reflecteix un conjunt de disposicions que tenen força de llei per a l''organització i el govern d''una entitat pública, d''una societat, d''una col·lectivitat, etc. Es tracta del règim intern de regulació de les relacions personals i patrimonials d''una societat civil, mercantil o cooperativa', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Cessament', 'TD01-013', 1, 'Document que constata la finalització de la prestació de serveis d’una persona que fins a aquell moment està en possessió d''un càrrec concret o realitza una funció determinada', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Decret legislatiu', 'TD01-014', 1, 'Norma amb rang de llei dictada pel Govern de les Illes Balears, prèvia delegació del Parlament, en una llei de bases o en una llei ordinària (en el cas de que es tracti de refondre textos legals) i que no pot afectar matèries reservades a llei orgànica. NOTA. Es tracta del document original del decret llei, no del document de treball', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Decret llei', 'TD01-015', 1, 'Disposicions legislatives provisionals aprovades pel Govern amb caràcter d’urgència NOTA. Dins d’un termini de trenta dies hàbils, el Parlament els pot validar o els pot derogar. Si el termini fineix sense que el Parlament els hagi validat, resten derogats Es tracta del document original del decret llei, no del document de treball', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Projecte de llei', 'TD01-016', 1, 'Document que conté prescripcions normatives presentat pel govern al Parlament per tal que aquest òrgan procedeixi a la seva elaboració, debat i votació', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Proposició de llei', 'TD01-017', 1, 'Document, que conté prescripcions normatives, presentat al Parlament per part d’òrgans titulars de la facultat d’iniciativa legislativa (amb exclusió del govern) per tal que procedeixi a la seva elaboració, debat i votació', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Esmena', 'TD63-001', 1, 'Proposta de modificació, supressió o addició en un projecte o proposició de llei o en un text parlamentariEn un document jurídic, rectificació perceptible d’errors materials, la qual ha de ser salvada al final', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Proposta de resolució', 'TD64-001', 1, 'Proposició de la decisió final sobre un procediment administratiu que l''òrgan instructor eleva a qui l''ha de resoldre. En l''àmbit del Parlament de les Illes Balears, el tipus fa referència a les propostes de resolució que poden presentar els grups parlamentaris  i la seva funció és impulsar l''acció política i de govern.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Moció', 'TD52-001', 1, 'Document emès per un o diversos grups municipals, grups parlamentaris o membres d''un organisme/ institució contra el govern o contra la seva direcció per una actuació, una decisió, etc. que es considera desencertada', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Moció de censura', 'TD01-018', 1, 'Document per mitjà del qual un parlament o un ple municipal pot exigir la responsabilitat política al poder executiu. La moció de censura parteix del propi Parlament o Ple, generalment d''algun grup de l''oposició política, en relació a l''acció de govern. La seva finalitat és aconseguir la retirada dels suports que té el President o l''Alcalde.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Qüestió de confiança', 'TD01-019', 1, 'Document generat durant el procediment en el qual el govern  comprova si continua tenint la confiança i el recolzament del Parlament o del Ple de l''Ajuntament per continuar desenvolupant el seu programa o una política concreta, generalment es produeix en una situació de crisi política', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Prec', 'TD01-020', 1, 'Document on es formula una (o varies) proposta d''actuació dirigida a determinats òrgans de govern durant l''activitat parlamentària o en el si del Ple d''un ajuntament amb la voluntat que sigui debatuda per tots els membres, encara que no se sotmet a votació', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pregunta parlamentària o al Ple', 'TD59-001', 1, 'Document que recull qualsevol qüestió plantejada als òrgans de govern en el si del Parlament o del Ple d''un ens local per part dels parlamentaris, dels grups municipals o dels ciutadans amb la finalitat d''aclarir un assumpte determinat.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llei', 'TD51-001', 1, 'Document que recull la norma jurídica, de caràcter general i obligatori, dictada pels òrgans que tenen atribuït el poder legislatiu, promulgada -en nom del rei- pel president/a del Govern de les Illes Balears i publicada al Butlletí Oficial de les Illes Balears, moment en què entra en vigor.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Avantprojecte de llei', 'TD01-021', 1, 'Proposta provisional d''un projecte de llei feta per un individu o una comissió designada pel govern, que serveix a la comissió parlamentària corresponent per a redactar el projecte definitiu', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Resposta parlamentària o al Ple', 'TD61-001', 1, 'Document que recull la resposta a les qüestions plantejades als òrgans de govern en el si del Parlament o del Ple d''un ens local per part dels parlamentaris, dels grups municipals o dels ciutadans amb la finalitat d''aclarir un assumpte determinat', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acord', 'TD02-001', 2, 'Document que reflecteix una decisió mútuament acceptada entre parts interessades.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acord de govern', 'TD02-002', 2, 'Document que reflecteix la decisió de l''administració pública adoptada preferentment per un òrgan col·legiat (per exemple, el Consell de Ministres o un consell executiu)', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acord del Ple', 'TD02-003', 2, 'Document, on es formalitza un acte administratiu resolutori provinent del Govern o dels Òrgans de govern de les corporacions locals, pel qual els ens locals poden avenir a les demandes judicials, fer transaccions sobre els seus béns o drets i/o sotmetre a arbitratge les conteses que se suscitin sobre els mateixos.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acord d''iniciació', 'TD02-004', 2, 'Acte administratiu de tràmit en què l''òrgan competent d''una administració pública inicia d''ofici un procediment administratiu per iniciativa pròpia com a conseqüència de l''ordre d''un òrgan superior, d''una petició raonada d''un altre òrgan o d''una denúncia', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Contracte', 'TD03-001', 3, 'Document consensual entre persones naturals o jurídiques, que reflecteix un acord entre diferents parts i que comporten unes obligacions que les diferents parts assumeixen quan tinguin per objecte l''execució d''obres, la gestió de serveis públics i els subministraments, quan així ho declari expressament una llei o quan estiguin vinculats directament al desenvolupament d''un servei públic, amb independència del seu contingut', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Garantia', 'TD03-002', 3, 'Contracte, accessori del principal, que té per finalitat assegurar al creditor el compliment de l''obligació.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Plec de clàusules', 'TD03-003', 3, 'Document que inclou les bases generals i amb caràcter previ que ha d''establir l''administració o ens públics , per adjudicar en contracte administratiu una obra o un servei públic, etc. NOTA. Altrament denominat Plec de condicions.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Plica', 'TD03-004', 3, 'Sobre clos que conté una documentació que no ha de llegir-se o publicar-se fins a un temps o una data determinats, segellat i diligenciat per qui dóna fe del seu contingut.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Contracte de treball', 'TD03-005', 3, 'Document que evidencia el vincle que s''estableix entre una persona física o jurídica, que actua com a empresari, i una persona física, que actua com a treballador, i pel qual es comprometen a intercanviar una prestació econòmica per una prestació professional, iniciant així una relació laboral.NOTA. També anomenat Contracte laboralAquest tipus engloba les diverses modalitats contractuals existents en la normativa vigent', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Oferta econòmica', 'TD03-006', 3, 'Proposició unilateral en termes econòmics que una persona física o jurídica fa a una altra per establir un contracte', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pòlissa', 'TD03-007', 3, 'Document mercantil que l''assegurador lliura al prenedor mitjançant el qual es declara formalment l''existència del contracte d''assegurança i les condicions en què s''ha pactat. Integren la pòlissa les condicions generals, les particulars i, si escau, les especials i els annexos que les complementen o modifiquen. La pòlissa de l''assegurança pot ser a l''ordre o al portador.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Conveni', 'TD04-001', 4, 'Document consensual que reflecteix un acord entre diferents parts i que comporta unes obligacions que les diferents parts assumeixen. Acord de col·laboració entre institucions', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pacte', 'TD04-002', 4, 'Document de consentiment entre dues o més persones o institucions que adquireixen un cert compromís. Per exemple, Pacte per la inclusió social', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Declaració', 'TD05-001', 5, 'Document que reflecteix una manifestació oral, escrita o mímica realitzada per l''administració, per una persona física o jurídica sota jurament o promesa a requeriment d''una autoritat judicial o administrativa o voluntàriament davant de notari', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Declaració d''activitats', 'TD05-002', 5, 'Document amb el qual la persona declarant assenyala els seus béns, drets i obligacions, a la vegada que fa referència també als béns, drets i obligacions dels cònjuges o convivents i d''altres familiars de primer grau amb el seu consentiment prèvi.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Declaració patrimonial i d''interessos', 'TD05-003', 5, 'Document mitjançant el qual la persona declarant indica les activitats professionals, mercantils o industrials que exerceix ell mateix i els cònjuges o convivents, així com altres familiars de 1er grau de consanguinitat i que, de conformitat amb legislació vigent, poden ésser causa d''incompatibilitat, no considerades incompatibles o enteses com d''exercici compatibleNOTA. Inclou també la Declaració complementària que incideix en les activitats professionals mercantils o industrials dels familiars fins al 2n grau de consanguinitat o afinitat que ocupen llocs a l''Administració de la CAIB o a les entitats dependents', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Declaració d''impacte ambiental', 'TD05-004', 5, 'Pronunciament de l''autoritat competent de medi ambient respecte de la repercussió previsible d''una activitat projectada i de la conveniència o no de realitzar-la.NOTA.La declaració d''impacte ambiental inclou, en cas de pronunciar-se a favor d''una activitat determinada, les condicions que aquesta ha d''acomplir per a la protecció adequada del medi ambient i dels recursos naturals', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Providència', 'TD05-005', 5, 'La providència en l''àmbit administratiu (providència d''Alcaldia, per exemple, en el món local) formalitza l''acte administratiu que mostra la voluntat d''iniciar o impulsar un expedient a partir d''uns fets motivadors, o demanar l''emissió dels corresponents informes o estudis. Pot ser una forma habitual d’iniciar un expedient.La Providència judicial és una resolució dictada per un jutge o per un tribunal per a la tramitació d’un afer.En l''àmbit tributari la provisió de constrenyiment és l’acte de L’Administració que ordena l’execució contra el patrimoni de l’obligat al pagament', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Avís', 'TD06-001', 6, 'Document que informa d’un fet a les persones interessades perquè n’estiguin assabentades i obrin en conseqüència.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Carta', 'TD06-002', 6, 'Document bàsic de comunicació usat habitualment per a les comunicacions escrites de caràcter interpersonal i de contingut general que no queden previstes com a components de la tramitació d''un procediment administratiu', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Circular', 'TD06-003', 6, 'Disposició administrativa dictada pels òrgans superiors de l’Administració sobre l’organització interna i el funcionament dels serveis que en depenen.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Comunicat', 'TD06-004', 6, 'Document en què es comunica d’una manera oficial i breu determinada informació.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Convocatòria', 'TD54-001', 6, 'Document per mitjà del qual es sol·licita l''assistència d''una persona o un col·lectiu de  persones a una sessió d''un òrgan col·legiat i on s''especifica el lloc, la data, l''hora i l''ordre del dia de la mateixa.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Invitació', 'TD06-005', 6, 'Document mitjançant el qual es convida a algú a assistir a un acte públic.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Ofici', 'TD06-006', 6, 'Document monotemàtic pel qual l''Administració comunica de manera oficial continguts relacionats amb la tramitació d’un procediment administratiu a altres òrgans de l''Administració o bé als administrats.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Saluda', 'TD06-007', 6, 'Document de caràcter protocol·lari usat per comunicacions breus realitzat per alts càrrecs oficials,els temes del qual no es refereixin a matèries reglades', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Ordre del dia', 'TD55-008', 6, 'Document que recull una relació ordenada dels assumptes que han d’ésser tractats en una sessió d''un òrgan col·legiat, una corporació local o un altre organisme', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Full de servei', 'TD06-009', 6, 'Document on es deixa constància de l’hora d’inici i finalització d''un servei, així com de qualsevol incidència o fet rellevant que s’hagi produït durant el servei.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Citació', 'TD07-001', 7, 'Document oficial pel qual es requereix la compareixença dels administrats davant de l''administració per dur a terme un tràmit administratiu.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Notificació', 'TD07-002', 7, 'Document mitjançant el qual és comunicada, d''una manera autèntica, la resolució judicial o administrativa a una persona determinada o al seu representant legal i a partir de la qual comença a córrer el termini per complir-la, exercitar accions o interposar recursos. NOTA. També denominat Cèdula.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Requeriment', 'TD07-003', 7, 'Document d''instigació, realitzada mitjançant una autoritat pública, a una persona perquè executi o s''abstingui d''executar un acte determinat', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Advertiment', 'TD07-004', 7, 'Avís sense l''amenaça d''una sanció. Avís sense comminació.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Anunci', 'TD08-001', 8, 'Document pel qual es fa pública una informació d’interès general', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Ban', 'TD08-002', 8, 'Disposició d''aplicació general al terme municipal dictada per l’autoritat local.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Edicte', 'TD08-003', 8, 'Document publicat per l''autoritat fent ús de les seves atribucions o donant compliment a un altre precepte legal, amb la finalitat de promulgar una disposició, fer pública alguna resolució, donar noticia de la celebració d''un acte o citar algú', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Nota de premsa', 'TD08-004', 8, 'Document enviat als mitjans de comunicació amb la finalitat de convocar-los a actes i conferències de premsa o de fer-los conèixer alguna informació susceptible d’ésser publicada o transmesa sobre temes, persones i/o productes de caire divers.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Publicació', 'TD08-005', 8, 'Document editat -generalment en múltiples exemplars i en diferents suports (analògic o electrònic)- o elaborat ambl''objectiu de ser editat, que té la finalitat de fer una difusió general. NOTA. Aquest engloba un full editat (un díptic, un tríptic, un fulletó...), un llibre editat (un llibre, una revista...), un cartell , un butlletí, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Publicació oficial (BOIB)', 'TD08-006', 8, 'Mitjà de què es dota l''Administració Pública de les Illes Balears per a fer públics els documents que, d''acord amb l''ordenament jurídic, i en virtut dels principis constitucionals de publicitat de les normes i de seguretat jurídica, han d''ésser objecte de publicació oficial', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Justificant de recepció', 'TD09-001', 9, 'Document a través del qual es declara haver rebut una carta, una tramesa, etc. NOTA. També denominat Acusament de rebuda o Avís de rebuda.Inclou el justificant de tramesa de documentació', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Rebut', 'TD09-002', 9, 'Document signat en què es reconeix haver rebut d’algú diners o alguna altra cosa, generalment com a pagament d’un producte o servei adquirit.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta', 'TD10-001', 10, 'Document formal en què es relata una acció, un esdeveniment de naturalesa jurídica o una sessió d''un òrgan col·legiat. Es pot distingir entre les actes d''òrgans col·legiats i les referides a accions concretes que constitueixen la formalització d''un tràmit', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta de compareixença', 'TD10-002', 10, 'Document necessari per resoldre la sol·licitud de declaració d''edifici ruïnós per part del propietari', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta de recepció', 'TD10-003', 10, 'Document on queda constància de l''entrega d''una obra per part del constructor al promotor una vegada conclosa. L’acta ha d’incloure: les parts que intervenen, la data del certificat final de l’obra o de la fase completa, el cost final de l’execució material de l’obra, la declaració de la recepció de l’obra amb o sense reserves i les garanties que s’exigeixin al constructor', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta d''inspecció', 'TD10-004', 10, 'Documents formals, i declaracions de coneixement, realitzats per funcionaris públics amb capacitat inspectoraNOTA. Diferents exemples d''actes d''inspecció són l''acta d''inspecció de treball, l''acta inspecció de consum, l''acta inspecció de turisme o l''acta d''inspecció tributària.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta de liquidació', 'TD10-005', 10, 'Document de liquidació de dèbits a la seguretat social en casos de falta d''afiliació o d''alta a la seguretat social, diferències de cotització, entre altres. Aquest tipus inclou les denominades actes de conformitat generades per l''administració d''hisenda. Document que també respon a l''acte resolutori en matèria de gestió tributària a través del qual es determina l''import de deute tributari o la quantitat a retornar o compensar a la persona interessada, d''acord amb les operacions de quantificació necessàries.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Analítica', 'TD10-006', 10, 'Document que evidencia un procés pel qual s''han recollit, mesurat i analitzat dades corresponents a una mostra orgànica o inorgànica, a la comptabilitat o als accessos i seguretat informàtics', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta d''ocupació', 'TD10-007', 10, 'Document, generat en el moment de lliurar un bé o un domini al seu destinatari, que és necessari, juntament amb el justificants de pagament, per inscriure al Registre de la Propietat i/o a d''altres registres públics la transmissió de propietat o domini per expropiació i també per a la cancel·lació, si s''escau, de les càrregues, els gravàmens i els drets reals de tota mena que afectin el bé o domini expropiat', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Presa de possessió', 'TD10-008', 10, 'Document pel qual, un cop s''han acomplert tots els tràmits del procediment de selecció corresponent, la persona nomenada per desenvolupar les tasques, prèviament definides, pren possessió d''un càrrec públic o d''una plaçaTambé coneguda com a Acta de presa de possessió', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta de comprovació d''obres', 'TD10-009', 10, 'Document pel qual es fa constar que l''obra finalitzada s''ha executat d''acord amb el projecte aprovat o amb l''autorització atorgada a un tercer per a realitzar-la i s''ajusta a allò establert pel plec de clàusules administratives. L''aprovació de l''acta de comprovació d''obres comporta l''autorització per a la seva obertura a l''ús públic, l''inici del termini de garantia de l''obra executada per part de tercers i l''inici de la fase d''explotació de l''obra', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta d''avaluació', 'TD10-010', 10, 'Dins l''entorn acadèmic, document que reflecteix, de forma nominal o alfabètica, el grau d’aprenentatge que un alumne ha assolit, tant al llarg del curs acadèmic com en proves concretes. En el cas d''educació no universitària es  fan constar les matèries cursades i les seves qualificacions, tant les superades com les no superades. I dins del camp professional, document de valoració de la idoneïtat o capacitació per al desenvolupament d''unes determinades funcions, d''un o més professionals, emès per una comissió de credencials o una junta d''avaluació.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acreditació', 'TD11-001', 11, 'Document on s''autoritza a una persona a representar a un organisme o estat.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Cèdula d''habitabilitat', 'TD11-002', 11, 'Document administratiu que acredita que un habitatge és apte per ser destinat com a residència de les persones i té les condicions tècniques d''habitabilitat, segons la normativa vigent.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Cèdula hipotecària', 'TD11-003', 11, 'Document referent a l''obligació pròpia d’entitats de crèdit, caracteritzada pel fet que el pagament d’interessos i l’amortització del capital tenen com a garantia específica els préstecs hipotecaris concedits per les esmentades entitats.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Certificat', 'TD11-004', 11, 'Document expedit per un funcionari públic competent o per una persona autoritzada legalment que dóna fe d''un fet, del contingut d''un document o de les circumstàncies que consten en arxius, registres, llibres d''actes, etc', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Certificat mèdic', 'TD11-005', 11, 'Document que recull l''informe mèdic, públic i oficial, redactat per un metge en un imprès oficial, on consten dades personals i l''estat de salut del sol·licitant en el moment de la petició, destinat a produir efectes davant d''un tercer i amb l''objecte de donar fe de la dada mèdica.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Escriptura', 'TD11-006', 11, 'Document en el qual es fa constar un acte o negoci jurídic de l''administració pública o d''un particular. L''escriptura pública és l''atorgada davant notari, el qual en dóna fe, signant-la i arxivant-la en el seu protocol, i es considera escriptura privada si és obra de particulars sense intervenció de cap funcionari que en doni fe pública', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Guia', 'TD11-007', 11, 'Document que obté qui fa ús d’una arma o transporta alguns gèneres', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Justificant', 'TD11-008', 11, 'Document que acredita un pagament, una apel·lació o oposició. NOTA. Inclou el Comprovant entés com el document que serveix per justificar el compliment d''una obligació i, per tant, també inclou les evidències (generalment en xml) que els sistemes d''informació generen automàticament quan es fa una acció necessària pel procés de tramitació (consulta de dades a la PICA, p.ex.).', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Justificant de presència', 'TD11-009', 11, 'Document que acredita que una persona ha estat en un lloc concret a una hora determinada', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Nota simple', 'TD11-010', 11, 'Document de caràcter informatiu elaborat pel Registre de la Propietat que proporciona informació breu i concisa sobre la situació jurídica d’una finca.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Protocol notarial', 'TD11-011', 11, 'Conjunt d''instruments públics i altres documents que són incorporats cada any, atorgats davant d''un mateix notari, amb numeració estrictament correlativa i enquadernats en acabar l''any en un o diversos volums.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Certificat de dades bancàries', 'TD11-012', 11, 'Document pel qual l’entitat bancària certifica que el compte indicat està obert, operatiu i a nom de la persona, empresa o institució que hi consten com a creditors i mitjançant el qual s’autoritzen ingressos per transferència bancàriaNOTA.  Altrament conegut com Certificat de compte bancari o Declaració responsable de veracitat de les dades bancàries aportades', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Carta de pagament', 'TD11-013', 11, 'Document expedit per l''administració, o en el seu cas elaborat pel propi contribuent, per tal que aquest pugui realitzar el pagament de tributs en qualsevol entitat col·laboradora autoritzada per a la recaptació i pugui acreditar l''ingrés de l''import a pagar.Les cartes de pagament validades pels òrgans competents o per entitats autoritzades tenen, per tant, la funció de justificar la realització del pagament per part del contribuent.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Alta de la incapacitat temporal- ILT', 'TD11-014', 11, 'Documentació que constata que el treballador, després d''haver estat un període de temps de baixa laboral, ja es troba capacitat per treballar', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Baixa per incapacitat temporal- ILT', 'TD11-015', 11, 'Document que constata la situació del treballador en la que es troba temporalment incapacitat per dur a terme les tasques assignades segons el seu lloc de treball i per la qual rep, entre d''altres, una prestació d’assistència sanitària garantida pel sistema', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Incapacitat permanent', 'TD11-016', 11, 'Document que certifica la incapacitat permanent d''un treballador, el qual -un cop recuperat del tractament mèdic- presenta reduccions anatòmiques o funcionals greus que en minven o n’anul·len la capacitat laboral', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Fulls de nòmina', 'TD11-017', 11, 'Document justificatiu del pagament dels salaris, deduccions o retencions econòmiques que l''Administració o altres organismes lliura als seus treballadors per a la prestació dels seus serveis laborals i on s''especifiquen els diferents conceptes retributiusNOTA. Altrament conegut com Fulls de salari', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llista de nòmina', 'TD11-018', 11, 'Relació del personal d''una empresa en què s''especifica amb detall la retribució que correspon a cadascú segons categories i conceptesAltrament conegut com a Llistat de nòmina.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Cartilla professional', 'TD11-019', 11, 'Document on s’anoten certes dades professionals referents a una determinada persona, pot incloure els cursos de formació, la categoria professional, els períodes d’ocupació etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Atestat', 'TD12-001', 12, 'Document que conté un conjunt de diligències  practicades per la policia judicial a l''entorn d''un presumpte delicte.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Diligència', 'TD12-002', 12, 'Document o certificat intern  mitjançant el qual l''Administració fa constar internament l''execució d''un fet o d''una actuació determinada', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Dictamen', 'TD57-001', 13, 'Document que reflecteix una opinió motivada que emet un facultatiu sobre un assumpte de la seva especialitat i, especialment, un jurista sobre una qüestió de dret o de procediment. NOTA. Rep el nom d''Informe, si l''escrit no exposa una opinió o si aquesta no és motivada o es limita a una exposició de fets', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Estudi', 'TD13-001', 13, 'Document científic, més detallat que l''informe, realitzat amb el principal objectiu d’estudiar o aprofundir un punt o un tema determinats per tal de poder dictar una resolució administrativa.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Full d''apreuament', 'TD13-002', 13, 'Document en què un perit fa constar el valor del bé o dret que s''ha d''expropiar, per presentar-lo a l''expedient del propietari afectat per l''expropiació', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Informe', 'TD13-003', 13, 'Document per mitjà del qual s''exposen de forma ordenada tots els elements tècnics i jurídics relatius a un assumpte que cal tenir en compte per resoldre de manera encertada un procediment. NOTA. S''anomena Dictamen quan l''òrgan emissor introdueix els seus propis judicis de valor i les seves opinions subjectives a fi d''assessorar o aconsellar l''òrgan que el sol·licita. Poden ésser preceptius o facultatius i vinculants o no vinculants', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Ponència', 'TD13-004', 13, 'Document relatiu a una comunicació o proposta sobre un assumpte científic o projecte de llei que se sotmet a l''examen i resolució en el si d''un organisme, assemblea, congrés, parlament, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recomanació', 'TD13-005', 13, 'Document pel qual un organisme, institució o autoritat reconeguda o de referència aconsella i/o emet directrius no vinculants sobre un determinat assumpte', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Sol·licitud', 'TD14-001', 14, 'Document per mitjà del qual l''administrat, en nom propi o d''altri, s''adreça a una altra administració pública per demanar quelcom en base a un dret subjectiu o interès legítim. O bé document pel qual una administració demana a una altra quelcom per poder continuar el procediment administratiuNOTA. La normativa vigent usa preferentment el terme Sol·licitud quan es refereix a la iniciació d''un procediment de forma sistemàtica. Altrament denominada Instància', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Demanda', 'TD15-001', 15, 'Document presentat davant el jutge per iniciar un plet en el qual s’exerciten una o diverses accions', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Denúncia', 'TD15-002', 15, 'Document en una persona o col·lectiu deixa constància que ha presenciat o que té coneixement d’un fet sancionable i s''adreça a l''autoritat competent per tal de notificar-ho', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Reclamació', 'TD15-003', 15, 'Document que expressa una protesta per una injustícia soferta o per una irregularitat que pot ésser-li perjudicial, per exigir-ne la reparació. En aquest sentit, és un tipus diferenciat de queixa, ja que inclou una reparació NOTA. És un tipus diferenciat de Queixa, ja que inclou una reparació del perjudici causat. També inclou la Reclamació prèvia entesa com a document administratiu de reclamació que cal formular com a requisit previ a l''exercici davant la jurisdicció civil o laboral de tota classe d''accions contra l''administració fonamentades en el dret privat o laboral', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Querella', 'TD15-004', 15, 'Document d''acusació presentat davant el jutge contra algú que hom fa reu d''un delicte i pel qual l''agreujat demana que sigui castigat i perseguit', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Queixa', 'TD15-005', 15, 'Document pel qual es manifesta el descontentament per alguna cosa; especialment per un comprador o un consumidor quan un producte adquirit o un servei rebut no ha respost a les expectatives que generava o que s''esperaven', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Al·legació', 'TD16-001', 16, 'Document pel qual s''invoca un fonament de dret en un procediment administratiu o un fet en un procediment jurídic.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Escrit de recusació', 'TD16-002', 16, 'Document sobre les actuacions de persones interessades en un procediment administratiu de demanar que no hi intervingui una autoritat o una persona determinada que està al servei d''una administració pública, perquè hi ha un possible interès d''aquesta en el procediment que es tramita o perquè hi ha participat com a pèrit o testimoni', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recurs', 'TD17-001', 17, 'Document per mitjà del qual una persona expressa la seva disconformitat amb una resolució administrativa i sol·licita a l''Administració que la revisi, i si és el cas, la modifiqui o l''anul·li, perquè  la considera perjudicial per als seus interessos', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recurs de queixa', 'TD17-002', 17, 'Document de recurs que pot interposar l''interessat quan l''administració no compleix amb el deure de dictar resolució expressa com a conseqüència d''una petició prèviament presentada o quan el procediment té defectes de tramitació, especialment si suposen paralització, infracció de terminis o omissió de tràmits esmenables. Aquest s''interposa davant del jutge o tribunal superior contra els actes resolutoris i els proveïments de l''inferior denegatoris de l''admissió d''un recurs d''apel·lació o de cassació', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recurs contenciós-administratiu', 'TD17-003', 17, 'Document per mitjà del qual l’interessat expressa la seva disconformitat amb una resolució o acte administratiu i sol·licita a l''Administració que ho revisi. Aquest recurs es admissible sobre totes aquelles disposicions de caràcter general i actes expressos i presumptes de l’administració pública que posen fi a la via administrativa en tres supòsits: el cas que incideixin de manera directa o indirecta el procediment, en que determinin la impossibilitat de continuar el procediment o en que produeixin indefensió o un perjudici irreparable d’un dret o interès legítim. També és admissible el recurs contenciós-administratiu contra la inactivitat de l’administració', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recurs d''alçada', 'TD17-004', 17, 'Document per mitjà del qual una persona expressa la seva disconformitat amb una resolució o acte administratiu i sol·licita a l''Administració que ho revisi, ja que la considera perjudicial per als seus interessos. Aquelles resolucions i actes que no posin fi a la via administrativa, podran ser recorreguts a alçada davant el superior jeràrquic de l’òrgan que els va dictar. Transcorregut un mes de la resolució sense haver interposat el recurs, aquesta resolució serà ferma a tots els efectes', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recurs extraordinari de revisió', 'TD17-005', 17, 'Document per mitjà del qual una persona física o jurídica mostra la seva disconformitat amb una resolució o acte administratiu i sol·licita a l''Administració que la revisi. Aquest recurs en via administrativa es pot interposar contra els actes ferms davant de l’òrgan administratiu que els va dictar (i que serà el competent per resoldre) durant un termini de quatre anys i sempre que es compleixin les circumstancies establertes a l’article 125.1. de la Llei 39/2015', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recurs potestatiu de reposició', 'TD17-006', 17, 'Document per mitjà del qual el ciutadà expressa la seva disconformitat amb una resolució o acte administratiu i sol·licita a l''Administració que ho revisi. Els actes administratius que posen fi a la via administrativa poden ser recorreguts potestativament en reposició davant el mateix òrgan que els ha dictat o impugnats davant l’ordre jurisdiccional contenciós-administratiu', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Declaració responsable', 'TD18-001', 18, 'Document subscrit per la persona interessada en què declara, sota la seva responsabilitat, quecompleix els requisits establerts per la normativa vigent per accedir al reconeixement d’un dret o facultat o per al seu exercici, que disposa de la documentació acreditativa corresponent i que es compromet a mantenir-ne el compliment durant la vigència d’aquest reconeixement o exercici. En el marc d''un procediment administratiu, faculta a l''Administració pública competent per verificar les dades que conté la declaració.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Renúncia', 'TD18-002', 18, 'Document mitjançant el qual l''interessat finalitza un procediment civil o administratiu concret, de manera voluntària renunciant a exercir els seus drets en un futur', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Comunicat previ', 'TD18-003', 18, 'Document subscrit per la persona interessada a través del qual posa en coneixement de l’Administració pública competent fets o elements relatius a l’exercici d’un dret o a l’inici d’una activitat, fet que permet el reconeixement o l''exercici d''un dret o l''inici d''una activitat des del dia de la seva presentació. NOTA. S’acompanya, si escau, de la documentació necessària per al seu compliment de conformitat amb el que estableix la normativa sectorial.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Desistiment', 'TD18-004', 18, 'Document mitjançant el qual l''interessat finalitza un procediment administratiu concret, de manera voluntària, sense que per això vegi afectat l’exercici dels seus drets', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Compromís', 'TD18-005', 18, 'Contracte especial formalitzat en escriptura segons la qual dues o més persones (físiques o jurídiques) estipulen que una determinada controvèrsia existent entre elles sigui resolta per tercers, designats voluntàriament, amb l''acord d''acatar-ne la decisió o l''arbitratge', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Declaració censal d''alta', 'TD18-006', 18, 'Document a través del qual una persona física o jurídica comunica a l''administració tributària la seva alta al Cens d''Empresaris, Professionals i Retenedors, les modificacions o la seva baixa del cens. Així mateix, també té la funció de comunicar l''inici de les activitats econòmiques que desenvolupi, les modificacions o el cesament en les mateixes.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acceptació', 'TD18-007', 18, 'Document mitjançant el qual una persona física o jurídica deixa constància que accepta o bé una herència o bé la proposta de resolució de la seva sol·licitud a una subvenció, una beca o un ajut', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Factura', 'TD19-001', 19, 'Document que acompanya el lliurament de mercaderies o la prestació de serveis amb llurs característiques, preus, taxes i els totals corresponents', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Minuta', 'TD19-002', 19, 'Document pel qual els advocats i professionals d''altres sectors presenten els seus honoraris.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Currículum', 'TD20-001', 20, 'Document que engloba un conjunt de dades personals i mèrits acadèmics i professionals d’una persona que sol·licita un lloc de treball, una plaça, l’obtenció d’un premi.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Document Nacional d''Identitat', 'TD20-002', 20, 'Document personal i intransferible emès per l''Administració General de l''Estat que acredita la identitat i les dades personals que en ell apareixen, així com la nacionalitat espanyola del seu titular.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Memòria testamentaria', 'TD20-003', 20, 'Document que conté instruccions establertes pel testador i que al·ludeix un testament anterior, firmada per ell mateix en tots els seus fulls. NOTA. Altrament denominada Cèdula testamentària', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Passaport', 'TD20-004', 20, 'Document públic, personal, individual i intransferible, expedit pels òrgans competents de l''Administració General de l''Estat, que acredita fora  i dins el territori espanyol  la identitat i nacionalitat dels ciutadans espanyols residents o no residents.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Permís de conduir', 'TD20-005', 20, 'Document públic que acredita una autorització administrativa al seu posseïdor per circular amb vehicles a motor per la via pública', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pressupost', 'TD20-006', 20, 'Document que indica el còmput anticipat del que costarà un bé o servei, de les despeses i els ingressos d’un organisme per a un període de temps determinat', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Targeta d''Identitat d''Estranger (TIE)', 'TD20-007', 20, 'Document identificatiu únic i exclusiu destinat a  persones estrangeres en situació de permanència legal al territori espanyol. Permís de residència necessari per  desenvolupar activitats econòmiques, professionals o socials. NOTA. Inclou entre les seves dades el Número d''Identitat d''Estranger (NIE)', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Certificat d''empadronament', 'TD20-008', 20, 'Document que acredita la residència a un municipi recollint les dades personals, l’adreça i la data d’inscripció que consten al Padró Municipal d''HabitantsNOTA. Altres denominacions són Volant d''empadronament o Certificat de residència', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Certificat de convivència', 'TD20-009', 20, 'Document que acredita les dades personals, l’adreça i la data d’inscripció que consten al Padró Municipal d’Habitants i també les dades de les persones que figuren inscrites convivint amb la persona sol·licitant.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Targeta', 'TD20-010', 20, 'Document amb el qual un ciutadà pot acreditar una determinada qualitat personal. Per exemple, que és soci d''una entitat, d''un partit, alumne d''una facultat, o que té un títol professional, etc. NOTA. També conegut com a Carnet', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Targeta sanitària europea (TSE)', 'TD20-011', 20, 'Document que acredita el dret del seu titular a rebre les prestacions sanitàries que siguin necessàries, des del punt de vista mèdic, durant una estada temporal en territori de la Unió Europea, de l''Espai Econòmic Europeu o a Suïssa per motius de treball, estudis, turisme, d''acord amb la legislació del país d''estada.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Targeta sanitària individual (TSI)', 'TD20-012', 20, 'Document que identifica i acredita al ciutadà com a usuari del sistema públic de les Illes Balears mitjançant el codi d’identificació personal i permet l’accés als centres de la xarxa sanitària pública i als medicaments finançats pel Servei de Salut. NOTA. La targeta sanitària intel·ligent conté informació identificadora del titular, generalment les seves dades personals, i l''historial clínic', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Títol', 'TD20-013', 20, 'Document expedit que dóna fe que una persona determinada posseeix unes aptituds o capacitats segons els seus estudis i coneixements o que fa constar que es troba en una situació determinada. NOTA. En són exemples el títol universitari o el títol de família nombrosa.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Autorització de representació', 'TD20-014', 20, 'Autorització d’una persona física o jurídica perquè sigui representada per una altra persona amb capacitat d''actuar davant l’administració pública durant la tramitació d’un procés administratiu i/o judicial', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llibre de família', 'TD20-015', 20, 'Llibret facilitat a les persones per fer constar llur situació civil i el naixement dels seus fills. En el llibre de família s''inscriuen el matrimoni, naixements, defuncions, separacions i divorcis', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Targeta d''Identificació Fiscal', 'TD20-016', 20, 'Document, expedit per l''administració tributària, que acredita el Número d''Identificació Fiscal (NIF) de qualsevol persona física o jurídica en les seves relacions de natura o amb transcendència tributària. L''antecedent del qual és el Codi d''Identificació Fiscal (CIF), usat fins l''any 2008.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Permís de circulació de vehicles', 'TD20-017', 20, 'Document europeu harmonitzat, emès per la Direcció General de Trànsit, que identifica la titularitat de qualsevol classe de vehicle automòbil (agrícola, d''obres o de serveis) i que certifica que ha estat matriculat.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Número d''Identitat d''Estranger (NIE)', 'TD20-018', 20, 'Document que identifica unívocament a les persones estrangeres sigui quina sigui la seva situació administrativa. Aquest document pot atorgar-se d''ofici en aquells casos en els quals s''obre un expedient administratiu a la persona estrangera (ja que resulta necessari per a l''expedició o tramitació de documents o diligències). O bé, a sol·licitud de l''interessat, perquè té interessos econòmics, professionals o socials a l''Estat i necessita del número identificatiu per realitzar operacions comercials o gestions a nivell social o professional.NOTA. No s''ha de confondre amb la Targeta d''Identitat d''Estranger, que sí acredita la situació de residència legal a Espanya, tot i que entre les seves dades inclou el NIE.Generalment, l''administració el lliura en forma de certificat, tot i que té una entitat documental pròpia.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Testament', 'TD20-019', 20, 'Document, amb les formalitats legals adients, a través del qual una persona física regula la seva successió per causa de mort, instituint un o més hereus i, normalment, establint altres disposicions', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llibreta d''inscripció marítima', 'TD20-020', 20, 'Document amb una doble funció que permet identificar el mariner, ja que esdevé el document d’identitat del mariner i mantenir un registre dels embarcaments i desembarcaments del seu posseïdor.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Calendari', 'TD99-001', 21, 'Document que reflecteix la distribució d’un seguit d’activitats durant un any o durant un període de temps determinat. Inclou el calendari laboral, calendari acadèmic, calendari fiscal, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Carta de serveis', 'TD99-002', 21, 'Document descriptiu que mostra la totalitat dels serveis que ofereixen les organitzacions subjectes al dret administratiu. Es considera una eina de gestió de qualitat implementada pel govern del que depèn aquella entitat pública; i suposa un acord entre l''administració i el ciutadà, amb l''objectiu de satisfer una necessitat ciutadana.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Descripció de lloc de treball', 'TD99-003', 21, 'Document que proporciona informació objectiva de les funcions i les responsabilitats, la missió i la ubicació per a cadascun dels llocs de treball de l''organització', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Manual', 'TD99-004', 21, 'Document on s''exposen aspectes fonamentals i instruccions concretes sobre un assumpte, un servei, un producte determinats d''una manera àmplia i exhaustiva amb la finalitat d''oferir o rebre un servei o d''usar un producte', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Memòria', 'TD99-005', 21, 'Document on es relacionen i valoren el conjunt d''activitats i el resultat econòmic que ha desenvolupat un organisme concret durant un període determinat.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Organigrama', 'TD99-006', 21, 'Document que mostra la representació esquemàtica dels diferents elements del Govern de les Illes Balears, amb les seves funcions i la relació que hi ha entre ells.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pla', 'TD99-007', 21, 'Document que engloba un conjunt orgànic de directrius i mesures polítiques, econòmiques i organitzatives adreçades a resoldre un problema o una situació determinades. NOTA. Inclou la Guia de procediments.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pla de projecte', 'TD99-008', 21, 'Document que especifica què cal per assolir els objectius d''un projecte.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Programa', 'TD99-009', 21, 'Document que enuncia i defineix els objectius i propòsits d''una activitat industrial, financera, arquitectònica, política, artística, etc que es pot determinar en l''espai, el temps i els recursos.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Projecte', 'TD99-010', 21, 'Document explicatiu que aporta totes les dades tècniques necessàries d''un estudi per tal que aquest es pugui realitzar. Pot ser un projecte de caràcter acadèmic, laboral, d''obra artística, d''obra arquitectònica, d''instal·lació arquitectònica, entre alguns exemples.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Protocol', 'TD99-011', 21, 'Document, que engloba un conjunt de directrius, regles i procediments genèrics, utilitzat com a guia d''actuació davant d''un àmbit concret i que cal acomodar a cada circumstància específica. NOTA. Un exemple és el Protocol d''Interoperabilitat', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Memòria econòmica', 'TD99-012', 21, 'Document que exposa de forma desglossada i en profunditat el desenvolupament d''un projecte des del punt de vista econòmic o la situació econòmica d''un organisme', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Memòria justificativa', 'TD99-013', 21, 'Document que especifica la planificació, execució i finançament d''un projecte per a la seva aprovació, indicant les dades del sol·licitant, les dades econòmiques bàsiques (desglossament de la despesa global i altres aspectes troncals), identificació del projecte (títol i descripció) o la planificació i la realització del mateix (indicant la data prevista d''inici i de finalització), entre d''altres.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Memòria tècnica', 'TD99-014', 21, 'Document on es recullen tots els passos seguits per construir o resoldre un projecte tècnic, des del plantejament del problema inicial, fins a la consecució, prova i avaluació del prototip.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pla docent', 'TD99-015', 21, 'Document on es defineix l’estructura organitzativa d’una matèria concreta que s’integra en un pla d’estudis, en una formació o en un curs determinat. Hi podrem diferenciar, entre d’altres, la presentació de l’assignatura, els blocs de contingut i els criteris d’avaluació del docent.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pla d''estudis', 'TD99-016', 21, 'Document de reglamentació i programació dels estudis que cal acomplir amb vista a la consecució d''un títol, al seguiment d''uns cursos, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Horari', 'TD99-017', 21, 'Document de planificació que reflecteix la distribució d''activitats en les diferents hores d''una jornada, tant de treball, com docent, entre altres.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta d''arqueig', 'TD99-018', 21, 'Document que reflecteix la situació comptable de la Tresoreria, alhora que lliga aquesta situació amb els estats que faciliten els bancs sobre els comptes de les Illes Balears, verificant que la situació de Tresoreria s’adigui amb la informació comptable', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Albarà', 'TD99-019', 21, 'Document que normalment acompanya la mercaderia en ésser lliurada, que té com a finalitat acreditar el lliurament de la mateixa, i que, si és conformat, acredita la seva recepció.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Aval', 'TD99-020', 21, 'Document pel qual una persona física o jurídica respon de la solvència moral o econòmica d''una altra.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Balanç', 'TD99-021', 21, 'Document comptable establert sobre la base de l''agrupació de partides en l''actiu i en el passiu, d''acord amb un sistema de comptes determinat de la confrontació de les quals es pot deduir l''estructura patrimonial d''una organització en un moment concret i si aquesta treballa amb guany o amb pèrdua', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Document comptable. Autorització de la despesa', 'TD99-022', 21, 'Document pel qual s’acorda la realització d’una despesa a càrrec d’un crèdit pressupostari determinat sense sobrepassar-ne l’import pendent d’aplicació, reservant a tal fi la totalitat o una part del crèdit pressupostari disponible. NOTA. Els documents comptables també es combinen donant lloc a combinacions com AD, ADO, etc. Per tant, els documents comptables s''identificaran segons la fase en la que es trobin. En aquest cas els que es trobin a la primera fase, Autorització de la despesa', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Document comptable. Disposició', 'TD99-023', 21, 'Document pel qual queda formalitzada la reserva del crèdit per un import i condicions exactament determinats. NOTA. També anomenat Compromís de despesa. Els documents comptables també es combinen donant lloc a combinacions com AD, ADO, etc. Per tant, els documents comptables s''identificaran segons la fase en la que es trobin. En aquest cas s''inclouran tots els documents comptables que es trobin en la fase de Disposició.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Document comptable. Obligació', 'TD99-024', 21, 'Document on es deixa constància de l’operació de contreure en comptes els crèdits exigibles contra el Govern perquè ha estat acreditada satisfactòriament la prestació objecte de la ''disposició''. NOTA. Els documents comptables també es combinen donant lloc a combinacions com AD, ADO, etcètera. Per tant, els documents comptables s''identificaran segons la fase en la que es trobin. En aquest cas s''inclouran tots els documents comptables que es trobin en la fase d''Obligació.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Extracte de comptes', 'TD99-025', 21, 'Document que conté el resum de les dades d’un compte elaborat per conèixer l’estat en un moment determinat.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Manament de pagament', 'TD99-026', 21, 'Document comptable que reflecteix l''acte pel qual s''ordena el pagament d''una obligació reconeguda. NOTA. Altrament denominat Ordre de pagament o Pagament ordenat', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Manament d''ingrés', 'TD99-027', 21, 'Document comptable, comprovant de la gestió dels ingressos de l''Administració en concepte de recaptació tributària, finances contractuals, retencions de la Seguretat Social, etc. NOTA. Altrament denominat Ordre d''ingrés.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Memòria de comptes anuals', 'TD99-028', 21, 'Document que serveix per ampliar i complimentar el balanç dels comptes anuals, els canvis en el patrimoni net, les pèrdues i els guanys d''aquest patrimoni i els fluxos d''efectiu', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Pressupost públic', 'TD99-029', 21, 'Document que reflecteix el còmput anticipat de les despeses i els ingressos d''una organització pública durant un període de temps determinat.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Reserva de crèdit', 'TD99-030', 21, 'Document on es constaten les retencions que es practiquen sobre els crèdits pressupostaris per tal de garantir la seva disponibilitat quan sigui necessari dur a terme l’execució d’una despesa.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Xec', 'TD99-031', 21, 'Document de crèdit pel qual el titular d''un compte bancari dóna ordre de pagament a l''entitat bancària. NOTA. Altrament denominat Taló bancari', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Comanda', 'TD99-032', 21, 'Document que l''administració adreça a un proveïdor per formalitzar una petició d''un producte o un servei', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Autoliquidació', 'TD99-033', 21, 'Declaració en què l’obligat tributari (el contribuent, el substitut del contribuent i/o el responsable solidari) comunica a l’Administració les dades necessàries per a la liquidació del tribut i d’altres de contingut informatiu i realitza per si mateix les operacions de qualificació i quantificació necessàries per determinar i ingressar l’import del deute tributari o, si s’escau, determinar la quantitat que s’ha de retornar o compensar', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Declaració tributària', 'TD99-034', 21, 'Document presentat davant l’Administració tributària on es reconeix o es manifesta la realització d’un fet rellevant per a l’aplicació dels tributs NOTA. Actualment en la tramitació dels impostos que es gestionen s’utilitzen autoliquidacions. Anteriorment també es podia presentar una declaració tributària, per exemple, en el cas de l’impost de successions i donacions.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Compte de resultats', 'TD99-035', 21, 'Compte diferencial que recull les pèrdues i els guanys comptables que es deriven de l’activitat pròpia d’una unitat econòmica en un exercici. NOTA. Altrament dit Compte de pèrdues i guanysNo confondre amb Compte d’explotació entesa com la relació comptable que recull totes les entrades i sortides resultants de l’activitat de l’empresa en el procés de producció i comercialització', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'TC1. Butlletí de cotització a la seguretat social', 'TD99-036', 21, 'Document que mostra el pagament de la cotització al règim general i als règims especials de la Seguretat Social durant un període concret de temps', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'TC2. Butlletí de cotització a la seguretat social. Relació Nominal de Treballadors', 'TD99-037', 21, 'Document, relatiu al pagament de la cotització al règim general i especials, que indica la relació de treballadors pels quals s''ha cotitzat durant un termini de temps determinat', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Proposta de liquidació', 'TD99-038', 21, 'Document concret, derivat del procediment de gestió tributària i generat prèviament a la liquidació provisional o a l''autoliquidació, que determina l''import del deute tributari o la quantitat a retornar. L''administració posa en coneixement de la persona interessada la proposta de liquidació per a que pugui al·legar el que convingui al seu dret.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Lletra de canvi', 'TD99-039', 21, 'Document mercantil utilitzat com a instrument de crèdit o mitjà de pagament, en virtut del qual una persona (lliurador) mana de pagar a una altra (lliurat) una certa quantitat a l’ordre d’una tercera persona (prenedor), que pot ésser el mateix lliurador, en el lloc i el temps convinguts. La lletra de canvi incorpora la figura de l''endossament (acció per la qual el creditor canviari transmet a una altra persona el dret incorporat al títol, ordenant que s''efectuï el pagament a aquesta nova persona designada)', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Liquidació del pressupost', 'TD99-040', 21, 'Document que té com a finalitat conèixer l’activitat financera desenvolupada per l’administració pública o per una empresa, organisme, institució, fundació, etc. La liquidació del pressupost posa de manifest per a cada partida pressupostària els crèdits inicials, les seves modificacions i els crèdits definitius, les despeses autoritzades i compromeses, les obligacions reconegudes, els pagaments ordenats i el realitzats, en relació a les despeses, i les previsions inicials, les seves modificacions i les previsions definitives, els drets reconeguts i anul·lats, així com els recaptats nets, pel que fa als ingressos. A conseqüència d’aquesta liquidació es determinen els drets pendents de cobrament i les obligacions pendents de pagament, el resultat pressupostari de l’exercici, els romanents de crèdit i el romanent de la tresoreria.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta de replanteig', 'TD99-041', 21, 'Document d''inici d''obra que reprodueix fidelment al terreny les dimensions i formes indicades en els plànols que integren la documentació tècnica d''una obra. NOTA. També coneguda com a Acta de replantejament', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Certificat final d''obra', 'TD99-042', 21, 'Document on el director d''una obra certifica que s''ha acabat de l''execució de l''obra de la qual ha dirigit l''execució material i que ha controlat quantitativament i qualitativament la construcció del que s’ha edificat. També estableix que l''edificació és apunt per a la seva utilització adecuada segons les instruccions d’us i manteniment.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Estudi de seguretat i salut', 'TD99-043', 21, 'Document tècnic redactat durant el procés d''elaboració del projecte d''obra pel tècnic competent, designat pel promotor o pel coordinador en matèria de seguretat i salut, i que forma part del projecte d''execució d''obra.NOTA. Conté, com a mínim, la memòria descriptiva dels procediments, equips tècnics i mitjans auxiliars que han d''utilizar-se o la utilització dels quals es pugui preveure amb una identificació dels riscos laborals que puguin ser evitats i una relació dels riscos laborals que no puguin eliminar-se; el plec de condicions particulars; els plànols en els quals es desenvoluparan els gràfics i esquemes necessaris i el pressupost que quantifiqui el conjunt de despeses previstes per l''aplicació i execució de l''estudi.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Full d''assumeix', 'TD99-044', 21, 'Document, visat pel col·legi corresponent, que s''ha de presentar per poder demanar la llicència d''obresNOTA. Altrament conegut com a Full d''assumpció', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llibre d''incidències d''obra', 'TD99-045', 21, 'Document que serveix per realitzar el control i seguiment del Pla de seguretat i salut en el treball de l''obra pel que fa a la comprovació periòdica del compliment de les previsions contingudes en aquest', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llibre de l''edifici', 'TD99-046', 21, 'Document que recull la història de l’edifici, que inclou tota la documentació que fa referència a l’edifici, la relació de tots els agents que han intervingut en la seva construcció i totes les garanties, així com el seu manteniment.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Plànol', 'TD99-047', 21, 'Document que representa a escala un terreny, una població, la planta d''un edifici, un objecte, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Acta d''infracció', 'TD99-048', 21, 'Document de proposta de sanció que pot ser confirmada per l''administració de l''estat o de les comunitats autònomes que tinguin la competència en la execució de la normativa continguda a l''acta en l''àmbit de Treball i Seguretat Social', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Denúncia administrativa', 'TD99-049', 21, 'Document en que l''Administració deixa constància d''un fet sancionable, que serveix per notificar-ho a l''autor del fet i a l''autoritat. A partir d''aquesta denúncia, l''òrgan competent inicia d''ofici un procediment administratiu.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Plec de càrrecs', 'TD99-050', 21, 'Document que forma part d''un expedient sancionador o disciplinari on consten les acusacions fetes a la persona expedientada.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Plec de descàrrec', 'TD99-051', 21, 'Document en què consta l''oposició als càrrecs que figuren en un plec de càrrecs', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Catàleg', 'TD99-052', 21, 'Document administratiu consistent en una relació de béns per tal que restin sotmesos a un règim jurídic especial, normalment amb la finalitat de protegir-los.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Inventari', 'TD99-053', 21, 'Document on hi consten els béns i drets pertanyents a una persona física o jurídica.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llista', 'TD99-054', 21, 'Document on es mostra una sèrie enumerativa de noms de persones o de coses, d’adreces o d’altres indicacions.NOTA. Inclou el Llistat, entés com el document resultat d''operacions efectuades per un ordinador, escrit en paper per una impressora.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Padró', 'TD99-055', 21, 'Document que reflecteix el llistat públic i autoritzat on es relacionen tots els habitants o béns amb finalitats determinades', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Registre', 'TD99-056', 21, 'Document en què s’anoten regularment cert ordre de fets a controlar. Al llibre de registre es fan constar les anotacions d''escrits, documents, comunicacions i altres per acreditar la seva presentació', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Repertori', 'TD99-057', 21, 'Document que mostra les matèries, ordenades de manera analítica, per tal de facilitar i simplificar la seva cercaNOTA. Un exemple pot ser un repertori de medicaments segons una o diferents matèries mèdiques', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Estadística', 'TD99-058', 21, 'Document que mostra un conjunt de dades recollides, classificades, analitzades d''acord amb mètodes matemàtics, i interpretades, relatives a un conjunt d''elements', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Fitxa', 'TD99-059', 21, 'Document normalitzat i estructurat on consten dades objectives sobre les característiques tècniques d''un element o es recullen els antecedents policials d''una persona per exemple', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Índex electrònic', 'TD99-060', 21, 'Document, signat electrònicament per l''administració, òrgan o entitat actuant, que assegura la integritat de l''expedient electrònic, recull el conjunt de documents associats a aquest -generalment en el moment del seu tancament- i garanteix la seva recuperació sempre que sigui precís', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Etiqueta', 'TD99-061', 21, 'Document que recull la informació essencial d''un producte a fi de facilitar-ne la identificació i conèixer la composició, les característiques, l''aplicació, la quantitat, l''ús, la conservació, el preu, la caducitat, etc. NOTA. Exemples d''aquest tipus són etiquetes de productes alimentaris, etiquetes de medicaments, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Bases', 'TD99-062', 21, 'Document que mostra un conjunt de normes que regulen els requisits i els procediments que han de complir i acceptar els participants en una convocatòria, sorteig, subhasta, concurs, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Examen', 'TD99-063', 21, 'Document relatiu a la prova escrita o oral a la qual és sotmès un candidat per declarar si és apte per entrar a una escola, per obtenir un grau o un càrrec, per aprovar una assignatura, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Qüestionari', 'TD99-064', 21, 'Document que reflecteix un conjunt de preguntes sobre un tema determinat, en una enquesta, un examen, una prova, una declaració judicial, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Addenda', 'TD99-065', 21, 'Document addicional a un contracte, a un conveni, a pressupostos, etc. mitjançant el qual les parts poden modificar, ampliar o concretar els termes de les obligacions contretes, sense necessitat d’elaborar-ne un de nou. És a dir, un document mitjançant el qual una persona, organisme o empresa pot adherir-se al contracte o conveni del qual en deriva', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Llista de control', 'TD99-066', 21, 'Document que serveix per comprovar que es compleix un conjunt de requisits legals, tecnològics, etc. i per avaluar-ne el seu grau de compliment en relació a uns estàndards o unes directrius determinades', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Entrevista', 'TD99-067', 21, 'Document generat en l’interrogatori d’un procés de treball. El document, en general, és la transcripció de l’entrevista realitzada. Per aportar proves fefaents, el document, ha d’anar vinculat a un document sonor o audiovisual (ex: gravació de l’entrevista)', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Executòria', 'TD99-068', 21, 'Document públic i solemne en què es consigna una sentència ferma. També inclou el document tramés per un tribunal o jutge que ha dictat una sentència a un inferior per a que practiqui les diligències d''execució', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Instrucció judicial', 'TD99-069', 21, 'Document previ a la vista judicial que presenten les parts', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Interlocutòria', 'TD99-070', 21, 'Document on es reflecteix una resolució judicial motivada que decideix recursos contra provisions, qüestions incidentals, supòsits processals, la nul·litat del procediment, o que, d’acord amb les lleis d’enjudiciament, ha de tenir aquesta forma', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Manament judicial', 'TD99-071', 21, 'Document provinent d’un òrgan jurisdiccional que té com a finalitat ordenar el lliurament de certificats o testimoniances, o la pràctica de qualsevol diligència judicial l’execució de la qual correspongui a registradors de la propietat, mercantils, de vaixells, de vendes a terminis de béns mobles, notaris, corredors col·legiats de comerç, o agents de jutjats o tribunals, en matèria civil; o a notaris, registradors de la propietat, auxiliars i subalterns de jutjats o tribunals i funcionaris de la policia judicial, en matèria penal. Els manaments per a l''anotació preventivadels embagaments expedits per òrgans de recaptació i adreçats al registres de la propietat durant el procediment de constrenyiment tenen el mateix valor que els manaments judicials', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Provisió', 'TD99-072', 21, 'Document que conté una resolució dictada per un jutge o una jutgessa, o per un tribunal, que decideix sobre qüestions de tràmit o sobre peticions secundàries o accidentals, tret que es limiti a aplicar normes d’impuls processal o que la llei exigeixi expressament la forma d’interlocutòria.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Sentència', 'TD99-073', 21, 'Document mitjançant el qual es presenta una resolució judicial subscrita pel jutge que decideix definitivament el plet o la causa en qualsevol instància o recurs i que posa fi a un litigi civil, una causa criminal o un incident', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Qüestió d''inconstitucionalitat', 'TD99-074', 21, 'Document resultant del procés de consulta promogut per part de qualsevol jutge o tribunal ordinari davant del Tribunal Constitucional quan considerin que una norma, amb rang de llei aplicable al procés judicial que duen a terme i la validesa del qual en depengui la decisió que han d’adoptar, pugui ser contrària a la Constitució.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recurs d’empara', 'TD99-075', 21, 'Recurs interposat davant el Tribunal Constitucional contra actes definitius de l’administració o sentencies de tribunals fermes i que s’estima que vulneren el drets i llibertats fonamentals reconeguts en la Constitució', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Recurs d’inconstitucionalitat', 'TD99-076', 21, 'Recurs que el govern de les Illes Balears interposa davant el Tribunal Constitucional contra lleis, disposicions normatives i actes amb força de llei que s’estima que vulneren alguna disposició continguda a la Constitució', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Liquidació de condemna', 'TD99-077', 21, 'Fixació del període de temps concret en el que la persona sentenciada a una pena privativa de llibertat restarà a presó', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Fotografia', 'TD99-078', 21, 'Imatge fixa captada per mitjà de la llum i de substàncies químiques o a través de procediments digitals.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Enregistrament sonor', 'TD99-079', 21, 'Emmagatzematge d''informació de caràcter sonor en un suport físic per conservar-la i poder-la reproduir posteriorment', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Enregistrament d''imatge en moviment', 'TD99-080', 21, 'Emmagatzematge de dades relatives a imatges en moviment en un suport físic per conservar-les i poder-les reproduir posteriorment', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Guió', 'TD99-081', 21, 'Document que exposa de forma succinta un tema, una conferència, un discurs, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_TIPUSDOCUMENTAL (ID, QUADRETIPUSDOCUMENTAL_ID, NOM, CODI, TIPUSNTI_ID, DEFINICIO, INICI, MODIFICACIO, ESTAT) VALUES(ACH_TIPUSDOCUMENTAL_SEQ.NEXTVAL, 1, 'Presentació', 'TD99-082', 21, 'Document relatiu a una comunicació sobre un tema científic o tècnic exposat davant d''un auditori, en una reunió, assemblea, congrés, conferència, etc.', SYSDATE, SYSDATE, 'ESBORRANY');
INSERT INTO ACH_BUTLLETI(ID, CODI, NOM, DESCRIPCIO) VALUES (ACH_BUTLLETI_SEQ.NEXTVAL, 'BOE', 'Butlletí Oficial de l''Estat', 'El Butlletí Oficial de l''Estat (BOE) és el diari oficial nacional espanyol dedicat a la publicació de determinades lleis, disposicions i actes d''inserció obligatòria. La seva edició, impressió, publicació i difusió està encomanada, en règim de descentralització funcional, a l''Agència Estatal Butlletí Oficial de l''Estat.');
INSERT INTO ACH_BUTLLETI(ID, CODI, NOM, DESCRIPCIO) VALUES (ACH_BUTLLETI_SEQ.NEXTVAL, 'BOIB', 'Butlletí Oficial de les Illes Balears', 'El Butlletí Oficial de les Illes Balears (BOIB) és el diari oficial de les Illes Balears. És a dir, la publicació oficial del Govern Balear que recull la legislació i normativa de l''administració pública insular, produïda a través de les lleis del Parlament de les Illes Balears, dels decrets del Govern i d''altres disposicions, que hi han de ser publicats perquè entrin en vigència');
INSERT INTO ACH_BUTLLETI(ID, CODI, NOM, DESCRIPCIO) VALUES (ACH_BUTLLETI_SEQ.NEXTVAL, 'DOUE', 'Diari Oficial de la Unió Europea', 'El Diari Oficial de la Unió Europea (DOUE), és la font oficial de el Dret comunitari europeu. L''entitat de la Unió Europea (UE) encarregada de la seva publicació és l''Oficina de Publicacions de la Unió Europea, situada a Luxemburg.');
